import React from 'react';    
import { Link } from 'react-router-dom';

class ModalDialogPopUp extends React.Component {  

    render() {
        // Type variable to store modal to display
        let modalContents = null; 

        if(this.props.modalType == "add"){
            console.log('add pressed');
            modalContents =
            <div class="modal" id="addStudent" header="Add" >
                <p id="modalDialogMessage">
                Do you want to add this student?
                <br></br><br></br></p>
                <Link to="/"><button id="dialog_yes_button"  onClick={this.props.hideModalDialogPopUp} >Yes</button></Link>
                <button id="dialog_no_button"modal="close"  onClick={this.props.hideModalDialogPopUp} >No</button>    
            </div>;
        }
        else if(this.props.modalType == "cancel"){
            modalContents =
            <div class="modal" id="cancelAddStudent" header="Cancel"  >
                <p id="modalDialogMessage">
                Cancel adding student?
                <br></br><br></br></p>
                <Link to="/"><button id="dialog_yes_button"  onClick={this.props.hideModalDialogPopUp}>Yes</button></Link>
                <button id="dialog_no_button" modal="close" onClick={this.props.hideModalDialogPopUp}>No</button> 
            </div>
        }
        return (  
        <div>
            <div id = "modal_yes_no_dialog_background">
                    {modalContents}
            </div>
        </div>
        );  
    }  
}  
export default ModalDialogPopUp;
